import React, { useEffect } from "react";
import HeroImage from "../components/HeroImage";
import "./Home.css";

const Home = () => {
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll(".content-section");

      sections.forEach((section) => {
        const sectionTop = section.getBoundingClientRect().top;
        if (sectionTop < window.innerHeight * 0.85) {
          section.classList.add("visible");
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll(); // Run on mount to check already visible sections

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="home">
      {/* Hero Section */}
      <HeroImage />

      {/* Content Section 1: About Me */}
      <div className="content-section">
        <div className="content-left">
          <img
            src={require("../assets/images/desklamp.jpg")}
            alt="Your Image"
          />
        </div>
        <div className="content-right">
          <h2>About Me</h2>
          <p>
            Hi, I'm Jashanjeet Singh, a passionate developer with a focus on
            creating beautiful and functional web experiences. I specialize in
            React, JavaScript, and modern web technologies.
          </p>
        </div>
      </div>

      {/* Content Section 2: My Skills (Alternate Layout) */}
      <div className="content-section alternate">
        <div className="content-right">
          <h2>My Skills</h2>
          <p>
            I have expertise in front-end development, UI/UX design, and
            responsive web design. I love turning ideas into reality through
            clean and efficient code.
          </p>
        </div>
        <div className="content-left">
          <img src={require("../assets/images/desklamp.jpg")} alt="Skills" />
        </div>
      </div>
    </div>
  );
};

export default Home;
