import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Header from './Header'; // Import the Header component
import './HeroImage.css';

// Import images
import image1 from '../assets/images/desklamp.jpg';
import image2 from '../assets/images/computerdeskman.jpg';
import image3 from '../assets/images/desklamp.jpg';

const images = [image1, image2, image3];

const HeroImage = () => {
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % images.length);
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  return (
    <div className="hero-section">
      {/* Hero Image */}
      <div className="hero-image">
        <AnimatePresence mode="wait">
          <motion.img
            key={currentImage}
            src={images[currentImage]}
            alt="Hero"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1.5 }} // Smooth transition
          />
        </AnimatePresence>
      </div>

      {/* Header and Hero Text */}
      <div className="hero-content">
        <Header /> {/* Header is now part of the hero section */}
        <div className="hero-text">
          <h1>A design studio for mighty small businesses and restaurants who are ready to brand up.</h1>
          <button className="portfolio-button">Portfolio</button>
        </div>
      </div>
    </div>
  );
};

export default HeroImage;
