import React from 'react';
import CertificationCarousel from '../components/CertificationCarousel';
import './About.css';

// Import local image
import yourImage from '../assets/images/desklamp.jpg';

const About = () => {
  return (
    <div className="about">
      {/* About Section */}
      <div className="about-section">
        <div className="about-text">
          <h2>About Me</h2>
          <p>
            Hi, I'm [Your Name], a passionate developer with a focus on creating
            beautiful and functional web experiences. I specialize in React,
            JavaScript, and modern web technologies. With a strong background in
            UI/UX design, I strive to deliver user-friendly and visually
            appealing solutions.
          </p>
        </div>
        <div className="about-image">
          <img src={yourImage} alt="Your Image" />
        </div>
      </div>

      {/* Certification Carousel */}
      <CertificationCarousel />
    </div>
  );
};

export default About;
