import React, { useState } from 'react';
import './CertificationCarousel.css';

// Import your certification images
import cert1 from '../assets/images/desklamp.png';
import cert2 from '../assets/images/clutereddesk.jpg';
import cert3 from '../assets/images/computerdeskman.jpg';

const CertificationCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const certifications = [
    { id: 1, image: cert1, title: 'React Certification' },
    { id: 2, image: cert2, title: 'JavaScript Expert' },
    { id: 3, image: cert3, title: 'UI/UX Design' },
    // Add more certifications as needed
  ];

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === certifications.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? certifications.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="carousel-container">
      <div
        className="carousel-track"
        style={{
          transform: `translateX(-${currentIndex * 100}%)`,
        }}
      >
        {certifications.map((cert, index) => (
          <div
            key={cert.id}
            className={`carousel-slide ${
              index === currentIndex ? 'active' : ''
            }`}
          >
            <div className="certification-card">
              <img src={cert.image} alt={cert.title} />
              <div className="certification-overlay">
                <h3>{cert.title}</h3>
                <button className="view-button">View Details</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CertificationCarousel;
