import React, { useState, useEffect, useRef } from "react";
import HeroImage from "../components/HeroImage";
import "./Home.css";

const Home = () => {
  // State and ref initialization
  const [animationIntensity, setAnimationIntensity] = useState(0.5);
  const sectionRefs = useRef([]);

  // Intersection Observer for scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible");
          }
        });
      },
      {
        threshold: 0.2,
        rootMargin: "0px 0px -100px 0px"
      }
    );

    sectionRefs.current.forEach((section) => {
      if (section) observer.observe(section);
    });

    return () => {
      sectionRefs.current.forEach((section) => {
        if (section) observer.unobserve(section);
      });
    };
  }, []);

  // Scroll intensity controller
  useEffect(() => {
    const handleScroll = () => {
      const intensity = Math.min(window.scrollY / 1000, 1);
      setAnimationIntensity(intensity);
      document.documentElement.style.setProperty(
        '--animation-intensity',
        intensity
      );
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="home" style={{ '--animation-intensity': animationIntensity }}>
      {/* Hero Section */}
      <HeroImage />

      {/* Content Section 1: About Me */}
      <div 
        className="content-section"
        ref={(el) => (sectionRefs.current[0] = el)}
      >
        <div className="content-left">
          <img
            src={require("../assets/images/desklamp.jpg")}
            alt="Workspace"
          />
        </div>
        <div className="content-right">
          <h2>About Me</h2>
          <p>
            Hi, I'm Jashanjeet Singh, a passionate developer with a focus on
            creating beautiful and functional web experiences. I specialize in
            React, JavaScript, and modern web technologies.
          </p>
        </div>
      </div>

      {/* Content Section 2: My Skills */}
      <div 
        className="content-section"
        ref={(el) => (sectionRefs.current[1] = el)}
      >
        <div className="content-left">
          <img
            src={require("../assets/images/desklamp.jpg")}
            alt="Skills"
          />
        </div>
        <div className="content-right">
          <h2>My Skills</h2>
          <p>
            I have expertise in front-end development, UI/UX design, and
            responsive web design. I love turning ideas into reality through
            clean and efficient code.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
