import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import './CertificationCarousel.css';

const certifications = [
  {
    id: 1,
    title: 'Certification 1',
    description: 'Learned advanced React concepts and best practices.',
    image: require('../assets/images/desklamp.jpg'),
  },
  {
    id: 2,
    title: 'Certification 2',
    description: 'Mastered JavaScript fundamentals and ES6+ features.',
    image: require('../assets/images/desklamp.jpg'),
  },
  {
    id: 3,
    title: 'Certification 3',
    description: 'Gained expertise in UI/UX design principles.',
    image: require('../assets/images/desklamo.jpg'),
  },
];

const CertificationCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextCertification = () => {
    setCurrentIndex((prev) => (prev + 1) % certifications.length);
  };

  const prevCertification = () => {
    setCurrentIndex((prev) => (prev - 1 + certifications.length) % certifications.length);
  };

  return (
    <div className="certification-carousel">
      <div className="carousel-content">
        <button onClick={prevCertification} className="carousel-button">
          <FaArrowLeft />
        </button>
        <div className="certification-details">
          <h3>{certifications[currentIndex].title}</h3>
          <p>{certifications[currentIndex].description}</p>
        </div>
        <button onClick={nextCertification} className="carousel-button">
          <FaArrowRight />
        </button>
      </div>
    </div>
  );
};

export default CertificationCarousel;
